INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(26, 'fr', 'vues-des-poles', '', 'Vues des pôles', '', '', NULL, '<p>L''année polaire internationale, dont c''était en 2008 la 4e édition depuis 1883, a mobilisé plusieurs milliers de scientifiques de 60 pays.</p>\n<p>Un concours photo organisé par l''Institut polaire IPEV (www.institut-polaire.fr), le CNRS et la Cité des Sciences et de l''Industrie a permis de recueillir des témoignages sur la recherche dans ces terres extrêmes.</p>\n<p>Libération.fr présente la sélection des 20 meilleurs clichés.</p>\n<p>URL originelle : http://www.liberation.fr/terre/1101652-vues-des-poles-des-scientifiques-derriere-l-objectif</p>', NULL, NULL, 1);
