INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(27, 'fr', 'la-balade-de-john-et-yoko', '', 'John et Yoko', '', '', NULL, '<p>Le Marché Biron à Saint-Ouen présente une exposition de 100 photographies du couple mythique John Lennon et Yoko Ono prises par Henry Pessar lors de leur passage à Paris en 1969. <strong>Jusqu''au 25 octobre 2009</strong>.</p>\n<p>Le fonds exceptionnel des 100 photos d''Henry Pessar constitue un témoignage d''un des tournants de la vie de John Lennon. L''une des photos prises par Henry Pessar a d''ailleurs été publiée en exclusivité pour illustrer le mariage de John Lennon et de Yoko Ono, prononcé dans l''intimité et incognito à Gibraltar le 20 mars 1969.</p>\n<p>Lennon et Ono, Le talent d''Henry Pessar, photographe de stars pour Paris Match, fait de cette exposition l''un des événements artistiques de la rentrée ! Lors du week-end d''inauguration de l''exposition, du 9 au 11 octobre, la musique des Beatles et de John Lennon sera diffusée dans tout le marché Biron afin de happer le public dans l''univers du plus célèbre groupe du monde.</p>\n<p>Le vernissage de l''exposition aura lieu le vendredi 9 octobre, jour d''inauguration du Mondial de l''Antiquité. L''exposition se prolongera jusqu''au lundi 25 octobre 2009 et sera ouverte au public les samedis, dimanches et lundis.</p>\n<p>Marché Biron - Puces de St Ouen<br />85 rue des Rosiers ou entrée avenue Michelet<br />93400 Saint Ouen</p>\n<p>Quelques clichés issues de l''exposition :</p>', NULL, NULL, 1);
