INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(28, 'fr', 'fiac-2009-paris', '', 'FIAC 2009 Paris', '', '', NULL, '<p>La 36e Foire Internationale d''Art Contemporain a ouvert ses portes jeudi au Grand Palais et à la Cour carrée du Louvre.</p>\n<p>Elle rassemble 198 galeries d''art moderne, d''art contemporain et 6 galeries de design, venues de 21 pays. Jusqu''au 25 octobre. Petit aperçu de ce qu''on peut y voir.</p>', NULL, NULL, 1);
INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(29, 'en', 'polar-bear-habitat', '', 'Polar Bear Habitat', '', '', NULL, '<p>The Interior Department on Thursday proposed designating more than 200,000 square miles of land, sea and ice along the northern coast of Alaska as critical habitat for the shrinking polar bear population.</p>\n<p>The area, the largest single designation of protected habitat for any species, encompasses the entire range of the two polar bear populations that exist on American land and territorial waters. Government scientists estimate that there are roughly 3,500 bears in the two groups, known the Chukchi Sea and the Southern Beaufort Sea populations.</p>\n<p>Officials said the bears’ range was shrinking because of the disappearance of sea ice linked to global warming.</p>\n<p>Original article on : http://www.nytimes.com/2009/10/23/science/earth/23bear.html?_r=1&amp;ref=science</p>', NULL, NULL, 1);
