INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(31, 'en', 'ionize-introduction', '', 'Ionize introduction', '', '', NULL, '<p>Ionize is a <a href="http://www.codeigniter.com">CodeIgniter</a> based CMS (Content Management System) based on PHP.</p>\n<p>Ionize is based on a simple data organization: <strong>"Every content is an article".</strong></p>\n<h3>Languages</h3>\n<p>At the top of data structure, languages.</p>\n<p>Everything can be translated, from dynamic database stored data to "static" elements directly displayed in templates (such as company footers adress).</p>\n<h3>Pages</h3>\n<p>Pages organize the content. They are used to build the navigation.</p>\n<p>Pages can be nested, to create a multi-level navigation.</p>\n<h3>Articles</h3>\n<p>Articles contains the textual content. An article can be a simple one or a blog post, depending on the way it is displayed.</p>\n<h3>Media</h3>\n<p>By media, we mean pictures, videos, files, music, everything which can be linked to an article.</p>\n<p>They are 2 ways to link a medi to an article :</p>\n<ul>\n<li><strong>Inline linking</strong> : That means a picture, for example, can be put directly into the article content, between two paragrph for example. In that case, the editor is responsible of the picture size. That means the editor has to take care the media size will not goes out from the website design.</li>\n<li><strong>Linking </strong>: A media linked to an article will be displayed as the "view" (the page or article template) has to display it. In that case, the editor don''t care about the media size or position,he just select an article view (picture diaporama for example) and the website will display the linked pictures in a correct way.</li>\n</ul>', NULL, NULL, 1);
INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(31, 'fr', 'ionize-introduction', '', 'Introduction à Ionize', '', '', NULL, '<p>Ionize est un CMS (Content Management System) basé sur <a href="http://www.codeigniter.com" target="_blank">CodeIgniter</a>.</p>\n<p>Ionize part d''un postulat simple : <strong>"Tout est article"</strong>.</p>\n<h3>Languages</h3>\n<p>At the top of data structure, languages.</p>\n<p>Everything can be translated, from dynamic database stored data to "static" elements directly displayed in templates (such as company footers adress).</p>\n<h3>Pages</h3>\n<p>Pages organize the content. They are used to build the navigation.</p>\n<p>Pages can be nested, to create a multi-level navigation.</p>\n<h3>Articles</h3>\n<p>Articles contains the textual content. An article can be a simple one or a blog post, depending on the way it is displayed.</p>\n<h3>Media</h3>\n<p>By media, we mean pictures, videos, files, music, everything which can be linked to an article.</p>\n<p>They are 2 ways to link a medi to an article :</p>\n<ul>\n<li><strong>Inline linking</strong> : That means a picture, for example, can be put directly into the article content, between two paragraph for example. In that case, the editor is responsible of the picture size. That means the editor has to take care the media size will not goes out from the website design.</li>\n<li><strong>Linking </strong>: A media linked to an article will be displayed as the "view" (the page or article template) has to display it. In that case, the editor don''t care about the media size or position,he just select an article view (picture diaporama for example) and the website will display the linked pictures in a correct way.</li>\n</ul>\n<p>Media can be linked to a page or an article.</p>\n<h3>Views</h3>\n<p>Views are template for displaying data. Powered by a tag language, templ