INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(36, 'en', 'game-over-project', '', 'Game Over Project', '', '', NULL, '<p>GAME OVER is one of the art projects developed by the French-Swiss artist Guillaume REYMOND (NOTsoNOISY  creative agency).<br />It consists of a series of collaborative animation movies which revive some of the very first video games.</p>\n<p>The pixels are replaced by a group of real human-beings that are moving from seat to seat in a theatre during about 4 to 6 hours. Each "pixel" has its own rules and decides what s/he wants to do for each picture. <br />Once all these pictures are turned into a short animation movie, a giant human-scale video game unfolds "live".</p>', NULL, NULL, 1);
INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(36, 'fr', 'projet-game-over', '', 'Projet Game Over', '', '', NULL, '<p>GAME OVER est l''un des projets artistiques développés par l''artiste franco-suisse Guillaume REYMOND (Atelier de création NOTsoNOISY). <br />Il consiste en une série de films d''animation collaboratifs réinterprétant quelques-uns des tout premiers jeux vidéo.</p>\n<p>Les pixels sont remplacés par un groupe d''humains (de 7 à 100 personnes) qui se déplacent de siège en siège dans une salle de spectacle, durant environs 4 à 6 heures. <br />Chaque "pixel" a ses propres règles et décide comment il/elle doit se déplacer image après image. Une fois toutes ces images mises en mouvement dans un court-métrage d''animation, se déroule une gigantesque partie de jeu vidéo à échelle humaine.</p>', NULL, NULL, 1);
INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(37, 'en', 'les-enceintes-magiques-de-david-murray', '', '', '', '', NULL, '', NULL, NULL, 1);
INSERT INTO `article_lang` (`id_article`, `lang`, `url`, `link`, `title`, `subtitle`, `meta_title`, `summary`, `content`, `meta_keywords`, `meta_description`, `online`) VALUES(37, 'fr', 'les-enceintes-magiques-de-david-murray', '', 'Les enceintes magiques de David Murray', '', '', NULL, '<p>David Murray est un grand du saxophone ténor. <br />L''artiste me fait l''effet d''un chaînon manquant entre John Coltrane et <a href="http://fr.wikipedia.org/wiki/Albert_Ayler">Albert Ayler</a>. Il n''aurait pas existé sans eux.</p>\n<p>Sa personnalité musicale indéniable culmine la scène depuis la fin des années 70.  Point d''orgue: le phénoménal <em>3D Family</em>, un Live à Willisau de 1978 <a href="http://www.dmute.net/chronique-album-22648_-_David-Murray_-_3D-Family.html">(HAT HUT Records)</a> : quatre faces  de bravoure catapultées par le batteur Andrew Cyrille,  qui remplaça Sunny Murray, (ce dernier avait dépensé les sous du billet  d''avion pour la Suisse).</p>\n<p>Quatre faces qui font péter les  enceintes.Singulièrement (autre paradoxe du saxophoniste californien qui  sonne comme un New-Yorkais), Murray a engendré peu d''imitateurs.  Surprenant? Essayez de reproduire la sonorité «<em>dirty</em>», les  suraigus grinçants, la technique ahurissante de sauts d''intervalles, le  grand écart entre la tradition et le Free jazz le plus expressionniste,  et enfin les voyages super-véloces en dehors des gammes!</p>\n<p>L''article original sur : <a href="http://jazz.blogs.liberation.fr/pfeiffer/2010/09/les-enceintes-magiques-de-david-murray.html" target="_blank">http://jazz.blogs.liberation.fr/pfeiffer/</a></p>', NULL, NULL, 1);
